// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract IDOPool is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    enum PoolState { Pending, Active, Completed, Refunding }
    
    IERC20 public immutable paymentToken;
    IERC20 public immutable offeringToken;
    uint256 public tokenPrice;     // Price in paymentToken per offeringToken
    uint256 public softCap;
    uint256 public hardCap;
    uint256 public minContribution;
    uint256 public maxContribution;
    
    uint256 public startTime;
    uint256 public endTime;
    uint256 public refundEndTime;
    PoolState public poolState;
    
    uint256 public totalRaised;
    mapping(address => uint256) public contributions;
    mapping(address => bool) public hasClaimed;
    mapping(address => bool) public hasRefunded;

    event TokensPurchased(address indexed buyer, uint256 amount);
    event RefundClaimed(address indexed user, uint256 amount);
    event PoolStateChanged(PoolState state);

    constructor(
        address _paymentToken,
        address _offeringToken,
        uint256 _tokenPrice,
        uint256 _softCap,
        uint256 _hardCap,
        uint256 _minContribution,
        uint256 _maxContribution
    ) {
        require(_paymentToken != address(0) && _offeringToken != address(0), "Invalid token addresses");
        require(_tokenPrice > 0, "Invalid token price");
        require(_softCap < _hardCap, "Invalid caps");

        paymentToken = IERC20(_paymentToken);
        offeringToken = IERC20(_offeringToken);
        tokenPrice = _tokenPrice;
        softCap = _softCap;
        hardCap = _hardCap;
        minContribution = _minContribution;
        maxContribution = _maxContribution;
    }

    function depositOfferingTokens(uint256 amount) external onlyOwner {
        require(poolState == PoolState.Pending, "Pool already started");
        offeringToken.safeTransferFrom(msg.sender, address(this), amount);
    }

    function startPool(
        uint256 _startTime,
        uint256 _endTime,
        uint256 _refundEndTime
    ) external onlyOwner {
        require(poolState == PoolState.Pending, "Already started");
        require(_startTime > block.timestamp, "Invalid start time");
        require(_endTime > _startTime, "Invalid end time");
        
        startTime = _startTime;
        endTime = _endTime;
        refundEndTime = _refundEndTime;
        poolState = PoolState.Active;
        emit PoolStateChanged(PoolState.Active);
    }

    function buyTokens(uint256 amount) external nonReentrant {
        require(poolState == PoolState.Active, "Not active");
        require(block.timestamp >= startTime && block.timestamp <= endTime, "Invalid time");
        require(amount >= minContribution && amount <= maxContribution, "Invalid amount");
        require(contributions[msg.sender].add(amount) <= maxContribution, "Exceeds max contribution");
        require(totalRaised.add(amount) <= hardCap, "Hard cap reached");

        paymentToken.safeTransferFrom(msg.sender, address(this), amount);
        contributions[msg.sender] = contributions[msg.sender].add(amount);
        totalRaised = totalRaised.add(amount);
        
        emit TokensPurchased(msg.sender, amount);
    }

    function finalize() external onlyOwner {
        require(poolState == PoolState.Active, "Invalid state");
        require(block.timestamp > endTime || totalRaised >= hardCap, "Pool not ended");
        
        poolState = totalRaised >= softCap ? PoolState.Completed : PoolState.Refunding;
        emit PoolStateChanged(poolState);
    }

    function claimTokens() external nonReentrant {
        require(poolState == PoolState.Completed, "Not completed");
        require(!hasClaimed[msg.sender], "Already claimed");
        
        uint256 tokens = contributions[msg.sender].mul(1e18).div(tokenPrice);
        hasClaimed[msg.sender] = true;
        offeringToken.safeTransfer(msg.sender, tokens);
    }

    function claimRefund() external nonReentrant {
        require(poolState == PoolState.Refunding, "Not refunding");
        require(block.timestamp <= refundEndTime, "Refund period ended");
        require(!hasRefunded[msg.sender], "Already refunded");
        
        uint256 amount = contributions[msg.sender];
        hasRefunded[msg.sender] = true;
        paymentToken.safeTransfer(msg.sender, amount);
    }

    function adminRefund() external onlyOwner {
        require(poolState == PoolState.Refunding, "Not in refund state");
        refundEndTime = block.timestamp + 7 days;
        emit PoolStateChanged(PoolState.Refunding);
    }

    function withdrawRaisedFunds() external onlyOwner {
        require(poolState == PoolState.Completed, "Not completed");
        require(block.timestamp > refundEndTime, "Refund ongoing");
        paymentToken.safeTransfer(owner(), paymentToken.balanceOf(address(this)));
    }
}

